%spark2

// Crearemos un Dataframe con la información obtenida del JSON
var df = spark.read.json("/tmp/santander/mediciones.json")
df.show()

%spark2

// Crearemos un Dataframe con la información obtenida del JSON
var df = spark.read.option("multiLine", "true").json("/tmp/santander/mediciones.json")
df.show()

%spark2

// uso de la función explode para obtener los elementos del array resources
var sensorDF = df.select(explode(df("resources")))
sensorDF.show()

%spark2

// Veamos cual es el esquema del Dataframe anterior
sensorDF.printSchema

%spark2

// Con lo que deberíamos poder seleciona aquellos elementos que nos interesan para nuestra "tabla"
var sensordataDF = sensorDF.select("col.ayto:carga","col.ayto:idSensor", "col.ayto:intensidad", "col.ayto:ocupacion", "col.dc:modified")
sensordataDF.show()

%spark2

// Registramos el DataFrame como una vista temporal
df.createOrReplaceTempView("santander_partitioned")
